import randomn=1000a=[]b=[]while len(a)<100:	x1= random.randint(0,1)	x2= random.randint(0,1)	x3= random.randint(0,1)	x4= random.randint(0,1)	x5= random.randint(0,1)	x6= random.randint(0,1)	if x1==x2 and x2==x3 and x3==x4 and x4==x5 and x5==x6:		y=random.randint(0,1)		a.append([x1,y])#print(a)for i in range(len(a)):	if a[i][0]==a[i][1]:		b.append([a[i][0],a[i][1]])print(len(b))